import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [appliances, setAppliances] = useState([]);

  useEffect(() => {
    axios.get("http://127.0.0.1:5000/get_appliances")
      .then((response) => setAppliances(response.data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  return (
    <div>
      <h2>Smart Home Assistant</h2>
      {appliances.length === 0 ? <p>Loading data...</p> : (
        <table>
          <thead>
            <tr>
              <th>Appliance</th>
              <th>Power Usage (W)</th>
              <th>Temperature (°C)</th>
              <th>Maintenance</th>
            </tr>
          </thead>
          <tbody>
            {appliances.map((appliance) => (
              <tr key={appliance.id}>
                <td>{appliance.appliance}</td>
                <td>{appliance.power_usage}</td>
                <td>{appliance.temperature}</td>
                <td>{appliance.days_since_last_service > 180 ? "⚠️ Required" : "✅ OK"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default App;
