import React, { useEffect, useState } from "react";

const App = () => {
  const [applianceData, setApplianceData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("http://127.0.0.1:5000/predict")
      .then((response) => response.json())
      .then((data) => {
        setApplianceData(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setLoading(false);
      });
  }, []);

  return (
    <div>
      <h1>Smart Home Assistant</h1>
      {loading ? (
        <p>Loading data...</p>
      ) : (
        <table border="1">
          <thead>
            <tr>
              <th>Appliance</th>
              <th>Power Usage (W)</th>
              <th>Temperature (Â°C)</th>
              <th>AI Predicted Maintenance</th>
            </tr>
          </thead>
          <tbody>
            {applianceData.map((appliance, index) => (
              <tr key={index}>
                <td>{appliance.appliance}</td>
                <td>{appliance.power_usage}</td>
                <td>{appliance.temperature}</td>
                <td>{appliance.maintenance_prediction}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default App;
